import React from 'react';
import '../Registration/Registration.css';
import { Link } from 'react-router-dom';

const otpAPI='https://api.randomuser.me/';

class Registration extends React.Component{
    constructor() {
        super();
        this.state = {
            fields: {},
            errors: {},
            loading: true,
            person: null,
            mobileno:''
        }
        this.handleChange = this.handleChange.bind(this);
        this.register = this.register.bind(this);
        this.otpclickhandler = this.otpclickhandler.bind(this);
        
      }
      otpclickhandler(){
        alert(this.state.fields.mobileno);
        console.log( this.state.fields.mobileno );
      }
      // async componentDidMount() {
      //   const url = "https://idea.truebook.in/tps_api/index.php?view=mobile_verify";
      //   const response = await fetch(url);
      //   const data = await response.json();
      //   this.setState({ person: data.msgcode, loading: false });
      // }
      hendleOtpGenerate(){
          let random = Math.floor(Math.random()*899999+100000);
        //   this.setState({
        //     otp:random      
        // });
      }
     
      handleChange(e) {
        let fields = this.state.fields;
        fields[e.target.email] = e.target.value;
        this.setState({
          fields
        });
      }
      
      register(e) {
        e.preventDefault();
    
      }
  
    render() {
      // if (this.state.loading) {
      //   return <div>loading...</div>;
      // }
  
      // if (!this.state.person) {
      //   return <div>didn't get a person</div>;
      // }
          return (
              
              <div className="register">
        
           {/* <div>{this.state.person.msgcode}</div> */}
                   <div className="background1"></div>
                  <div className="background3"></div>
                   <div className="registerForm"> 
                        <hgroup>
                        <h1>True Transport Register</h1>
                        {/* <span class="icon novi-icon icon-circle icon-bordered icon-lg icon-default mdi mdi-account-multiple-outline"></span> */}
                        </hgroup>
                        <form>
                        <div className="group">
                            <input type="text" placeholder="Name" name="name" className="tname" value={this.state.fields.name} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Username</label> */}
                           
                        </div>
                        <div className="group">
                            <input type="email" placeholder="Email" name="email" className="temail" value={this.state.fields.email} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Username</label> */}
                           
                        </div>
                        <div className="group">
                            <input type="text" placeholder="Mobile No" name="mobileno" className="tmobile" value={this.state.fields.mobileno} onChange={this.handleChange}/><span className="highlight"></span><span className="rbar"></span>
                            {/* <label>Password</label> */}
                            <button onClick={ this.otpclickhandler } className="btn btn-deluge btn-sm" id="verifymobileno" name="verifymobileno" type="button">Send OTP</button>
                            
                        </div>
                        <div className="group">
                            <input type="text" placeholder="Verify Code" name="verifyno" className="tvcode" value={this.state.fields.username} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                           
                        </div>
                        <div className="group">
                            <input type="password" placeholder="Password" name="password" className="tpass" value={this.state.fields.password} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            
                        </div>
                        <div className="group">
                            <input type="password" placeholder="Confirm Password" name="password" className="tpass" value={this.state.fields.password} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                           
                        </div>
                            <button onClick= {this.register} type="button" className="buttonui "> <span> Register </span>
                                <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                            </button>     
                        </form>
                        <div className="powered">
                        You have Already Account?<Link to="/" >Login</Link>
                        </div>
                    </div>
              </div>
            );
          }
}
export default Registration;
