import React, { Component } from 'react';

class Add_company extends Component {
    constructor(){
        super()
        this.state = {
            fields: {},
            errors: {}
        }
        this.handleChange = this.handleChange.bind(this);
        this.AddCompanySend = this.AddCompanySend.bind(this);
    }

    AddCompanySend(e){
        e.preventDefault();
        if(this.handleValidation){
            alert("perfect");
        }else{
            alert("error");
        }
    }
     /**VALIDATION ADD COMPANY FORM HERE..... */
     handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;
   
        //Name
        if(!fields["name"]){
           formIsValid = false;
           errors["name"] = "*Name is required";
        }
   
        if(typeof fields["name"] !== "undefined"){
           if(!fields["name"].match(/^[a-zA-Z]+$/)){
              formIsValid = false;
              errors["name"] = "Only letters Allow";
           }        
        }
   
        //Email
        if(!fields["email"]){
           formIsValid = false;
           errors["email"] = "*Email is Required";
        }
   
        if(typeof fields["email"] !== "undefined"){
           let lastAtPos = fields["email"].lastIndexOf('@');
           let lastDotPos = fields["email"].lastIndexOf('.');
   
           if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') === -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
              formIsValid = false;
              errors["email"] = "Email is not valid";
            }
       } 
     
   
       this.setState({errors: errors});
       return formIsValid;
   }
   
  
    handleChange(field, e){         
        let fields = this.state.fields;
        fields[field] = e.target.value;        
        this.setState({fields});
    }
    render()
    {
        return(
            <div className="Add_company">
                <div id="sc-add-company">
                    <h1>Add Company Details</h1>
                    <div className="sc-container">
                        <form className="add_company_form">
                            <input type="text" name="legal_entity_name" value={this.state.fields["name"]} onChange={this.handleChange.bind(this, "name")} placeholder="Name of Legal entity" />
                            <span className="errorMsg">{this.state.errors.name}</span>
                            <textarea name="address" value="" onChange="" placeholder="Address"></textarea>
                            <input type="text" name="city" value="" onChange="" placeholder="City" />
                            <input type="text" name="pan_no" value="" onChange="" placeholder="PAN NO" />
                            <input type="text" name="gst_no" value="" onChange="" placeholder="GST NO" />
                            <input type="text" name="cin_no" value="" onChange="" placeholder="CIN NO" />
                            <input type="text" name="email" value="" onChange="" placeholder="Email Id" />
                            <input type="text" name="mobile_no" value="" onChange="" placeholder="Contact Number" />
                            <div>
                            <input type="submit" onClick={this.AddCompanySend} className="save" value="Save" />
                            <input type="submit" className="save_close" value="Save & Next" />
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Add_company;
