import React from 'react';
import { Link } from 'react-router-dom';
import '../Login/Login.css';

class Login extends React.Component{
  constructor(props){
    super(props);

    this.state = {
        fields: {},
        errors: {}
    }
 }
     
 handleValidation(){
  let fields = this.state.fields;
  let errors = {};
  let formIsValid = true;

  //Name
   if(!fields["password"]){
      formIsValid = false;
      errors["password"] = "*Password is required";
   }


  // if(typeof fields["name"] !== "undefined"){
  //    if(!fields["name"].match(/^[a-zA-Z]+$/)){
  //       formIsValid = false;
  //       errors["name"] = "Only letters";
  //    }        
  // }

  //Email
  if(!fields["email"]){
     formIsValid = false;
     errors["email"] = "*Email is required";
  }

  if(typeof fields["email"] !== "undefined"){
     let lastAtPos = fields["email"].lastIndexOf('@');
     let lastDotPos = fields["email"].lastIndexOf('.');

     if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') === -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
        formIsValid = false;
        errors["email"] = "*Email is not valid";
      }
 }  

 this.setState({errors: errors});
 return formIsValid;
}

contactSubmit(e){
  e.preventDefault();
  if(this.handleValidation()){
     alert("Form submitted");
  }else{
     alert("Form has errors.")
  }

}

handleChange(field, e){         
  let fields = this.state.fields;
  fields[field] = e.target.value;        
  this.setState({fields});
}



    render() {
            
            return (
              <div className="login">
                  <div className="background"></div>
                  <div className="background2"></div>
                   <div className="loginForm"> 
                        <hgroup>
                        <h1>True Transport Login</h1>
                        </hgroup>
                        <form name="login" >

                        <div className="group">
                    <input ref="email" type="text" name="email" className="temail" placeholder="Enter Email or Phone No" 
                    value={this.state.fields["email"]} onChange={this.handleChange.bind(this, "email")}  />
                 <span style={{color: "red"}}>{this.state.errors["email"]}</span>
             
                        </div>


                        <div className="group">
                            <input ref="password" type="password" name="password" className="tpass" placeholder="Enter Password"
                           value={this.state.fields["password"]} onChange={this.handleChange.bind(this, "password")} /><span className="highlight"></span><span className="bar"></span>
                          <span style={{color: "red"}}>{this.state.errors["password"]}</span>
                          
                        </div>
                           {/* <button onClick={this.contactSubmit.bind(this)} type="button" className="buttonui "> <span> Login </span>
                                <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                            </button>         */}
                        </form>
                        <div className="powered">
                        <Link to="/register" >Create New Register</Link>
                        </div>
                    </div>
              </div>
            );
          }
}
/*** MAKE STYLE COMPONET IN LOGIN PAGE */
const LoginButton = () => 
<button onClick={this.contactSubmit.bind(this)} type="button" className="buttonui "> <span> Login </span>
<div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
</button>  
export default Login;
