import React from 'react';
import { Link } from 'react-router-dom';
import '../Login/Login.css';

class Login extends React.Component{
    constructor() {
        super();
        this.state = {
            fields: {},
            errors: {}
        }
        this.handleChange = this.handleChange.bind(this);
        this.login = this.login.bind(this);
      }
     
      handleChange(e) {
        let fields = this.state.fields;
        fields[e.target.email] = e.target.value;
        this.setState({
          fields
        });
      }
      
      login(e) {
        e.preventDefault();
        if (this.validateForm()) {
            let fields = {};
            fields["email"] = "";
            fields["password"] = "";
            this.setState({fields:fields});
            alert("Form submitted");
        }
  
      }
      validateForm() {

        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;
  
        if (!fields["email"]) {
          formIsValid = false;
          errors["email"] = "*Please enter your email-ID.";
        }
  
        if (typeof fields["email"] !== "undefined") {
          //regular expression for email validation
          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(fields["email"])) {
            formIsValid = false;
            errors["email"] = "*Please enter valid email-ID.";
          }
        }
  
        if (!fields["password"]) {
          formIsValid = false;
          errors["password"] = "*Please enter your password.";
        }
  
        if (typeof fields["password"] !== "undefined") {
          if (!fields["password"].match(/^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%&]).*$/)) {
            formIsValid = false;
            errors["password"] = "*Please enter secure and strong password.";
          }
        }
  
        this.setState({
          errors: errors
        });
        return formIsValid;
  
  
      }  
    render() {
       
            return (
              <div className="login">
                  <div className="background"></div>
                  <div className="background2"></div>
                   <div className="loginForm"> 
                        <hgroup>
                        <h1>True Transport Login</h1>
                        </hgroup>
                        <form name="login">
                        <div className="group">
                            <input type="email" name="email" className="temail" placeholder="Enter Email or Phone No" 
             value={this.state.fields.email} onChange={this.handleChange} /><span className="highlight"></span><span className="bar"></span>
                      <span className="errorMsg">{this.state.errors.email}</span>
                        </div>
                        <div className="group">
                            <input type="password" name="password" className="tpass" placeholder="Enter Password"
             value={this.state.fields.password} onChange={this.handleChange} /><span className="highlight"></span><span className="bar"></span>
                          <span className="errorMsg">{this.state.errors.password}</span>
                          
                        </div>
                        <button onClick={this.login} type="button" className="buttonui "> <span> Login </span>
                                <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                            </button>        
                        </form>
                        <div className="powered">
                        <Link to="/register" >Create New Register</Link>
                        </div>
                    </div>
              </div>
            );
          }
}
export default Login;
