import React from 'react';
import '../Registration/Registration.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
class Registration extends React.Component{
    constructor() {
        super();
        this.state = {
            fields: {},
            errors: {},
            name:'',
            email:'',
            mobileno:'',
            verifyno:'',
            password:'',
            re_password:'',
            loading:false,
            message:false

        }
        this.handleChange = this.handleChange.bind(this);
        this.sendMobileVerifyOtp = this.sendMobileVerifyOtp.bind(this);
      }

      /** START REGISTRATION VALIDATION HERE */
      registerValidation(e){
        e.preventDefault();
   
        if(this.handleValidation()){
           alert("Form submitted");
        }else{
           alert("Form has errors.")
        }
      }
      handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;
   
        //Name
        if(!fields["name"]){
           formIsValid = false;
           errors["name"] = "*Name is required";
        }
   
        if(typeof fields["name"] !== "undefined"){
           if(!fields["name"].match(/^[a-zA-Z]+$/)){
              formIsValid = false;
              errors["name"] = "Only letters Allow";
           }        
        }
   
        //Email
        if(!fields["email"]){
           formIsValid = false;
           errors["email"] = "*Email is Required";
        }
   
        if(typeof fields["email"] !== "undefined"){
           let lastAtPos = fields["email"].lastIndexOf('@');
           let lastDotPos = fields["email"].lastIndexOf('.');
   
           if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') === -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
              formIsValid = false;
              errors["email"] = "Email is not valid";
            }
       } 
            //mobileno
            if(!fields["mobileno"]){
            formIsValid = false;
            errors["mobileno"] = "*Mobile Number is required";
          } 
          if(typeof fields["mobileno"] !== "undefined"){
          if(!fields["mobileno"].match(/^[0-9]+$/)){
              formIsValid = false;
              errors["mobileno"] = "Only Digits Allow";
          }        
        }
      
        if(!fields["verifyno"]){
        formIsValid = false;
        errors["verifyno"] = "*Verify Code Required";
      } 
      if(typeof fields["verifyno"] !== "undefined"){
        if(!fields["verifyno"].match(/^[0-9]+$/)){
            formIsValid = false;
            errors["verifyno"] = "Only Digits Allow";
        }        
      }
      
      
      if(!fields["password"]){
        formIsValid = false;
        errors["password"] = "*Password Is Required";
      } 
      
      if(!fields["cpassword"]){
        formIsValid = false;
        errors["cpassword"] = "*Conform Password Is Required";
      } 
      
          this.setState({errors: errors});
          return formIsValid;
      }

       /**END REGISTRATION VALIDATION HERE */

      // handleChange(e) {
      //   this.setState({ [e.target.name]: e.target.value });
      // }

       /** START INPUT VALUE TO TARGET THIUS CODE HERE */
      handleChange(field, e){         
        let fields = this.state.fields;
        fields[field] = e.target.value;        
        this.setState({fields});
    }
      /** END INPUT VALUE TO TARGET THIUS CODE HERE */
      
        /** START SEND OTP API CALL */
      sendMobileVerifyOtp(e) {
        e.preventDefault();
        const mobileno= this.state.mobileno
        
        this.setState({
          loading:true,
          message: true
        })
        const passdata = mobileno
        
        let formData = new FormData();
        formData.append('mobile', passdata);
        let init = {
          method: 'POST',
          headers: { 'content-type': 'multipart/form-data' },
          mode: 'cors',
          cache: 'default',
          dataType: 'json',
          type:     'POST',
          async:    true,
          
        };
        alert(init);
        axios.post('/tps_api/index.php?view=mobile_verify',formData,init)
        .then(response => {
          console.log(response.data);
          alert(window.atob(response.data));
          alert(window.atob(response.data2));
          this.setState({
            loading:false,
            message: false
          })
        }).catch(err => {
          console.log(err);
          this.setState({
            loading:false
          })
        })
  
      }
  /** END SEND OTP API CALL */

  
    render() {
    
      return (
        <div className="register">
             <div className="background1"></div>
            <div className="background3"></div>
             <div className="registerForm"> 
                  <hgroup>
                  <h1>True Transport Register</h1>
                  {/* <span class="icon novi-icon icon-circle icon-bordered icon-lg icon-default mdi mdi-account-multiple-outline"></span> */}
                  </hgroup>
                  <form encType="multipart/form-data">
                  <div className="group">
                      <input ref="name" type="text" placeholder="Name" name="name" className="tname" onChange={this.handleChange.bind(this, "name")} value={this.state.fields["name"]}/><span className="highlight"></span><span className="bar"></span>
                      {/* <label>Username</label> */}
                      <span style={{color: "red"}}>{this.state.errors["name"]}</span>
                  </div>
                  <div className="group">
                      <input ref="email" type="email" placeholder="Email" name="email" className="temail" onChange={this.handleChange.bind(this, "email")} value={this.state.fields["email"]}/><span className="highlight"></span><span className="bar"></span>
                      {/* <label>Username</label> */}
                      <span style={{color: "red"}}>{this.state.errors["email"]}</span>
                  </div>
                  <div className="group">
                      <input ref="mobileno" type="number" placeholder="Mobile No" name="mobileno" className="tmobile" value={this.state.fields["mobileno"]} onChange={this.handleChange.bind(this,"mobileno")}/><span className="highlight"></span><span className="rbar"></span>
                      {/* <label>Password</label> */}
                      <button onClick={this.sendMobileVerifyOtp} className="btn btn-deluge btn-sm" id="verifymobileno" name="verifymobileno" type="button">Send OTP</button>
                      <span style={{color: "red"}}>{this.state.errors["mobileno"]}</span>
                  </div>
                  <div className="group">
                      <input  ref="verifyno" maxLength="6" type="text" placeholder="Enter OTP" name="verifyno" className="tvcode" value={this.state.fields["verifyno"]} onChange={this.handleChange.bind(this,"verifyno")}/><span className="highlight"></span><span className="bar"></span>
                      {/* <label>Password</label> */}
                      <span style={{color: "red"}}>{this.state.errors["verifyno"]}</span>
                  </div>
                  <div className="group">
                      <input  ref="password" type="password" placeholder="Password" name="password" className="tpass" value={this.state.fields["password"]} onChange={this.handleChange.bind(this,"password")}/><span className="highlight"></span><span className="bar"></span>
                      {/* <label>Password</label> */}
                      <span style={{color: "red"}}>{this.state.errors["password"]}</span>
                  </div>
                  <div className="group">
                      <input ref="cpassword" type="password" placeholder="Confirm Password" name="cpassword" className="tpass" value={this.state.fields["cpassword"]} onChange={this.handleChange.bind(this,"cpassword")}/><span className="highlight"></span><span className="bar"></span>
                      {/* <label>Password</label> */}
                      <span style={{color: "red"}}>{this.state.errors["cpassword"]}</span>
                  </div>
                      <button onClick={this.registerValidation.bind(this)}  type="button" className="buttonui "> <span> Register </span>
                          <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                      </button> 
                     
                  </form>
                  <div className="powered">
                  You have Already Account?<Link to="/" >Login</Link>
                  </div>
              </div>
        </div>
      );
          }
}
export default Registration;
