import React, { Component } from 'react';

class Add_company extends Component {
    constructor(){
        super()
        this.state = {
            fields: {},
            errors: {}
        }
        this.handleChange = this.handleChange.bind(this);
        this.AddCompanySend = this.AddCompanySend.bind(this);
    }

    AddCompanySend(e){
        e.preventDefault();
        if(this.handleValidation()){
            alert("perfect");
        }else{
            alert("error");
        }
    }
     /**VALIDATION ADD COMPANY FORM HERE..... */
     handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;
   
        //Name
        if(!fields["legal_entity_name"]){
           formIsValid = false;
           errors["legal_entity_name"] = "*Name is required";
        }
   
        //Email
        if(!fields["email"]){
           formIsValid = false;
           errors["email"] = "*Email is Required";
        }
   
        if(typeof fields["email"] !== "undefined"){
           let lastAtPos = fields["email"].lastIndexOf('@');
           let lastDotPos = fields["email"].lastIndexOf('.');
   
           if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') === -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
              formIsValid = false;
              errors["email"] = "Email is not valid";
            }
       } 
     
   
       this.setState({errors: errors});
       return formIsValid;
   }
   
  
    handleChange(field, e){         
        let fields = this.state.fields;
        fields[field] = e.target.value;        
        this.setState({fields});
    }
    render()
    {
        return(
            <div className="Add_company">
                <div id="sc-add-company">
                    <h1>Add Company Details</h1>
                    <div className="sc-container">
                        <form encType="multipart/form-data" className="add_company_form">
                            <div className="field_company">
                                <div className="labelname">Name Of Legal Entity:</div>
                                <input type="text" name="legal_entity_name" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="Name of Legal entity" />
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                            </div>
                            
                            <div className="field_company">
                            <div className="labelname">City:</div>
                                <input type="text" name="city" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="City" />
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                            </div>
                            
                            <div className="field_company">
                            <div className="labelname">PAN NO:</div>
                                <input type="text" name="pan_no" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="PAN NO" />
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                            </div>
                           
                            <div className="field_company">
                            <div className="labelname">GST NO:</div>
                                <input type="text" name="gst_no" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="GST NO" />
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                            </div>
                            
                            <div className="field_company">
                            <div className="labelname">CIN NO:</div>
                                <input type="text" name="cin_no" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="CIN NO" />
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                            </div>
                           
                            <div className="field_company">
                            <div className="labelname">Email:</div>
                                <input type="text" name="email" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="Email Id" />
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                            </div>
                           
                            <div className="field_company">
                            <div className="labelname">Mobile No:</div>
                                <input type="text" name="mobile_no" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="Contact Number" />
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                            </div>
                            <div className="clr"></div>
                                <div className="labelname">     Address:</div>
                                <textarea name="address" value={this.state.fields["legal_entity_name"]} onChange={this.handleChange.bind(this, "legal_entity_name")} placeholder="Address"></textarea>
                                <span className="errorMsgcompany">{this.state.errors.legal_entity_name}</span>
                           

                            <div className="btncompany">
                            <input type="submit" onClick={this.AddCompanySend.bind(this)} className="save" value="Save" />
                            <input type="submit" className="save_close" value="Save & Next" />
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Add_company;
