import React from 'react';
import { Link } from 'react-router-dom';
import '../Login/Login.css';

class Login extends React.Component{
    constructor(props) {
        super(props);
    
        this.state = {
          email: "",
          password: ""
        };
      }
     
    //  componentWillMount(){
    //     {this.state.email === "" && this.setState({
    //         nameerror:"Please Enter Email"
    //     })}
    //  }
     handleChange(field, e){         
        let email = this.state.email;
        alert(e.target.value);
        // email[email] = e.target.value;        
        // this.setState({email});
    }
    render() {
       
            return (
              <div className="login">
                  <div className="background"></div>
                  <div className="background2"></div>
                   <div className="loginForm"> 
                        <hgroup>
                        <h1>True Taxes Login</h1>
                        </hgroup>
                        <form >
                        <div className="group">
                            <input type="email" className="temail" placeholder="Enter Email or Phone No" 
             onChange={this.handleChange} /><span className="highlight"></span><span className="bar"></span>
                      
                        </div>
                        <div className="group">
                            <input type="password" className="tpass" placeholder="Enter Password"
             /><span className="highlight"></span><span className="bar"></span>
                          
                          
                        </div>
                        <button type="button" className="buttonui "> <span> Login </span>
                                <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                            </button>        
                        </form>
                        <div className="powered">
                        <Link to="/register" >Create New Register</Link>
                        </div>
                    </div>
              </div>
            );
          }
}
export default Login;
