import React, { Component } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import Profile from '../Profile/Profile';
/**STYLE COMPONET CSS HERE */
const LeftSidebar = styled.div`
width: 260px;
height: 100%;
position: fixed;
top: 0;
left: 0;
border: none;
bottom: 0;
z-index: 1;
box-shadow: 0 10px 30px -12px rgba(0, 0, 0, 0.42), 0 4px 25px 0px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2);
right: auto;
overflow-y: auto;
flex-direction: column;
display: flex;
outline: none;
flex: 1 0 auto;
background: linear-gradient(60deg, #66bb6a, #43a047);
`;

const Logo = styled.div`
padding: 15px 15px;
z-index: 4;
position: relative;
background: darkcyan;
`;

const LinkImg = styled.a`
color: #FFF;
display: block;
padding: 5px 0;
font-size: 18px;
text-align: left;
font-family: "Roboto", "Helvetica", "Arial", sans-serif;
font-weight: 400;
line-height: 30px;
text-transform: uppercase;
text-decoration: none;
background-color: transparent;
`;

const InSideLinkDiv = styled.div`
width: 30px;
display: inline-block;
max-height: 30px;
margin-left: 10px;
margin-right: 15px;
`;

const Icon = styled.img`
top: 15px;
width: 35px;
border: 0;
position: absolute;
vertical-align: middle;
`;

const MenuSideBar = styled.div`
width: 260px;
height: calc(100vh - 75px);
z-index: 4;
overflow: auto;
position: relative;
`;

const Ul = styled.ul`
position: unset;
list-style: none;
margin-top: 20px;
padding-top: 0;
padding-left: 0;
margin-bottom: 0;
padding-bottom: 0;
`;

const A = styled.a`
color: #FFF;
text-decoration: none;
`;

const MenuSpace = styled.div`
width: auto;
margin: 10px 15px 0;
display: block;
padding: 10px 15px;
position: relative;
transition: all 300ms linear;
font-family: "Roboto", "Helvetica", "Arial", sans-serif;
font-weight: 300;
line-height: 1.5em;
border-radius: 3px;
background-color: transparent;
box-sizing: border-box;
text-align: left;
align-items: center;
justify-content: flex-start;
text-decoration: none;
color: inherit;
border: 0;
cursor: pointer;
outline: none;
user-select: none;
vertical-align: middle;
-webkit-appearance: none;
-webkit-tap-highlight-color: transparent;
`;

const Name = styled.div`
color: #FFF;
margin: 0;
font-size: 14px;
font-family: "Roboto", "Helvetica", "Arial", sans-serif;
font-weight: 300;
line-height: 30px;
flex: 1 1 auto;
min-width: 0;
position: relative;
right: -46px;
top: 6px;
`;

const Span = styled.span`
top: 0;
left: 0;
width: 100%;
height: 100%;
display: block;
z-index: 0;
overflow: hidden;
position: absolute;
border-radius: inherit;
pointer-events: none;
`;

/**ANKIT HEADER CSS COMPONET HERE.... */
const Header = styled.header`
    height:70px;
    color: #555555;
    width: calc(100% - 260px);
    border: 0;
    display: block;
    padding: 10px 0;
    z-index: 1029;
    position: absolute;
    box-shadow: none;
    min-height: 50px;
    transition: all 150ms ease 0s;
    padding-top: 10px;
    border-bottom: 0;
    border-radius: 3px;
    margin-bottom: 0;
    background-color: #dbcccf;
    top: 0;
    left: auto;
    right: 0;
    box-sizing: border-box;
    flex-shrink: 0;
    flex-direction: column;

`;
const Button = styled.button`
  width: 38%;
  cursor: pointer;
  background: transparent;
  font-size: 16px;
  border-radius: 3px;
  color: palevioletred;
  border: 2px solid palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
  transition: 0.5s all ease-out;
  float: right;
  &:hover {
    background-color: palevioletred;
    color: white;
  }
`;

 const ImgPro = styled.img`
    float:right;
    height: 30px;
    margin: 10px;
    cursor: pointer;
    display: inline-block;
 `;

 const Dropdowncont = styled.div`
 display: none;
 background-color: #f9f9f9;
 min-width: 160px;
 box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
 z-index: 1;     
`;

 const Dropdown = styled.div`
      position: relative;
      display: inline-block;
      float: right;
      &:hover ${Dropdowncont}{
        display:block;
      }
 `;

 const Content = styled.div`
 margin-left: 260px;
 margin-top: 70px;
 padding: 5px 15px;
 `;



class Dashboard extends Component {
  logout(){
    sessionStorage.removeItem('formData');
    this.props.history.push("/");
  }

  render() {
    return (
      <div >
        {/* <nav>
          <button className="btn btn-primary" onClick={this.logout.bind(this)}>Logout</button>
        </nav> */}
        {/* <h2>Welcome to HomePage</h2> */}
        <LeftSidebar>
            <Logo>
              <LinkImg href="#">
                <InSideLinkDiv>
                  <Icon src="https://img.icons8.com/material/24/000000/flow-chart.png"/>
                </InSideLinkDiv>
                Creative Tim
              </LinkImg>
            </Logo>
            <MenuSideBar>
              <Ul>
                <A href="https://www.google.com/" target="blank">
                  <MenuSpace>
                    <Icon src="https://img.icons8.com/ios-glyphs/30/000000/user-male-circle.png"/>
                    <Name>Lorry Receipt (LR)</Name>
                    <Span></Span>
                  </MenuSpace>
                </A>
                <A href="#">
                  <MenuSpace>
                    <Icon src="https://img.icons8.com/ios-glyphs/30/000000/user-male-circle.png"/>
                    <Name>Invoice</Name>
                    <Span></Span>
                  </MenuSpace>
                </A>
                <A href="#">
                  <MenuSpace>
                    <Icon src="https://img.icons8.com/ios-glyphs/30/000000/user-male-circle.png"/>
                    <Name>Challans</Name>
                    <Span></Span>
                  </MenuSpace>
                </A>
                <A href="#">
                  <MenuSpace>
                    <Icon src="https://img.icons8.com/ios-glyphs/30/000000/user-male-circle.png"/>
                    <Name>Bank &Cash book</Name>
                    <Span></Span>
                  </MenuSpace>
                </A>
                <A href="#">
                  <MenuSpace>
                    <Icon src="https://img.icons8.com/ios-glyphs/30/000000/user-male-circle.png"/>
                    <Name>Reports</Name>
                    <Span></Span>
                  </MenuSpace>
                </A>
                <A href="#">
                  <MenuSpace>
                    <Icon src="https://img.icons8.com/ios-glyphs/30/000000/user-male-circle.png"/>
                    <Name>Master</Name>
                    <Span></Span>
                  </MenuSpace>
                </A>
              </Ul>
            </MenuSideBar>
        </LeftSidebar>


        <Header>
          <a>Add Company</a>
          <Dropdown>
                <ImgPro src="https://img.icons8.com/dusk/64/000000/contract-job.png"></ImgPro>
                <Dropdowncont>
                  <Link to="/profile"><Button>Profile</Button></Link>
                  <Button onClick={this.logout.bind(this)}>Logout</Button>
                </Dropdowncont>
          </Dropdown>
        </Header>
        
        <Content>
          <Profile/>
        </Content>
      </div>
    );
  }
 
}

export default Dashboard;
