import React from 'react';
import '../Registration/Registration.css';
import { Link } from 'react-router-dom';

class Registration extends React.Component{
    constructor() {
        super();
        this.state = {
            fields: {},
            errors: {}
        }
        this.handleChange = this.handleChange.bind(this);
        this.register = this.register.bind(this);
        
      }
      componentWillMount(){
        this.hendleOtpGenerate();
      }
     
      hendleOtpGenerate(){
          let random = Math.floor(Math.random()*899999+100000);
          alert(random);
          this.setState({
            otp:random      
        });
      }
     
      handleChange(e) {
        let fields = this.state.fields;
        fields[e.target.email] = e.target.value;
        this.setState({
          fields
        });
      }
      
      register(e) {
        e.preventDefault();
        if (this.validateForm()) {
            let fields = {};
            fields["name"] = "";
            fields["mobileno"] = "";
            fields["vericode"] = "";
            fields["email"] = "";
            fields["password"] = "";
            this.setState({fields:fields});
            alert("Form submitted");
        }
  
      }
      validateForm() {
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;
  
        if (!fields["name"]) {
          formIsValid = false;
          errors["name"] = "*Please enter your Name.";
        }
  
        if (typeof fields["name"] !== "undefined") {
          if (!fields["name"].match(/^[a-zA-Z ]*$/)) {
            formIsValid = false;
            errors["name"] = "*Please enter alphabet characters only.";
          }
        }
  
        if (!fields["email"]) {
          formIsValid = false;
          errors["email"] = "*Please enter your email-ID.";
        }
  
        if (typeof fields["email"] !== "undefined") {
          //regular expression for email validation
          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(fields["email"])) {
            formIsValid = false;
            errors["email"] = "*Please enter valid email-ID.";
          }
        }
  
        if (!fields["mobileno"]) {
          formIsValid = false;
          errors["mobileno"] = "*Please enter your mobile no.";
        }
  
        if (typeof fields["mobileno"] !== "undefined") {
          if (!fields["mobileno"].match(/^[0-9]{10}$/)) {
            formIsValid = false;
            errors["mobileno"] = "*Please enter valid mobile no.";
          }
        }
        if (!fields["verifyno"]) {
            formIsValid = false;
            errors["verifyno"] = "*Please enter your Verify no.";
          }
        if (typeof fields["verifyno"] !== "undefined") {
            if (!fields["verifyno"].match(/^[0-9]{10}$/)) {
              formIsValid = false;
              errors["verifyno"] = "*Please enter valid mobile no.";
            }
          }
        if (!fields["password"]) {
          formIsValid = false;
          errors["password"] = "*Please enter your password.";
        }
  
        if (typeof fields["password"] !== "undefined") {
          if (!fields["password"].match(/^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%&]).*$/)) {
            formIsValid = false;
            errors["password"] = "*Please enter secure and strong password.";
          }
        }
  
        this.setState({
          errors: errors
        });
        return formIsValid;
      } 
  
    render() {
    
            return (
              <div className="register">
                   <div className="background1"></div>
                  <div className="background3"></div>
                   <div className="registerForm"> 
                        <hgroup>
                        <h1>True Taxes Register</h1>
                        {/* <span class="icon novi-icon icon-circle icon-bordered icon-lg icon-default mdi mdi-account-multiple-outline"></span> */}
                        </hgroup>
                        <form>
                        <div className="group">
                            <input type="text" placeholder="Name" name="name" className="tname" value={this.state.fields.name} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Username</label> */}
                            <span className="errorMsg">{this.state.errors.name}</span>
                        </div>
                        <div className="group">
                            <input type="email" placeholder="Email" name="email" className="temail" value={this.state.fields.email} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Username</label> */}
                            <span className="errorMsg">{this.state.errors.email}</span>
                        </div>
                        <div className="group">
                            <input type="text" placeholder="Mobile No" name="mobileno" className="tmobile" value={this.state.fields.mobileno} onChange={this.handleChange}/><span className="highlight"></span><span className="rbar"></span>
                            {/* <label>Password</label> */}
                            <button onClick={this.hendleOtpGenerate()} className="btn btn-deluge btn-sm" id="verifymobileno" name="verifymobileno" type="button">Verify</button>
                            <span className="errorMsg">{this.state.errors.mobileno}</span>
                        </div>
                        <div className="group">
                            <input type="text" placeholder="Verify Code" name="verifyno" className="tvcode" value={this.state.fields.username} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            <span className="errorMsg">{this.state.errors.verifyno}</span>
                        </div>
                        <div className="group">
                            <input type="password" placeholder="Password" name="password" className="tpass" value={this.state.fields.password} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            <span className="errorMsg">{this.state.errors.password}</span>
                        </div>
                        <div className="group">
                            <input type="password" placeholder="Confirm Password" name="password" className="tpass" value={this.state.fields.password} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            <span className="errorMsg">{this.state.errors.password}</span>
                        </div>
                            <button onClick= {this.register} type="button" className="buttonui "> <span> Register </span>
                                <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                            </button>     
                        </form>
                        <div className="powered">
                        You have Already Account?<Link to="/" >Login</Link>
                        </div>
                    </div>
              </div>
            );
          }
}
export default Registration;
