import React from 'react';
import { Link } from 'react-router-dom';
import '../Login/Login.css';

class Login extends React.Component{
    constructor(props) {
        super(props);
    
        // reset login status
       // this.props.dispatch(userActions.logout());
    
        this.state = {
          username: '',
          password: '',
          submitted: false
        };
    
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
      handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
      }
    
      handleSubmit(e) {
        e.preventDefault();
    
        this.setState({ submitted: true });
        const { username, password } = this.state;
        const { dispatch } = this.props;
        if (username && password) {
         // dispatch(userActions.login(username, password));
        }
      }
    render() {
        const { loggingIn } = this.props;
        const { username, password, submitted } = this.state;
            return (
              <div className="login">
                  <div className="background"></div>
                  <div className="background2"></div>
                   <div className="loginForm"> 
                        <hgroup>
                        <h1>True Taxes Login</h1>
                        </hgroup>
                        <form >
                        <div className="group">
                            <input type="email" className="temail" placeholder="Enter Email or Phone No"  /><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Username</label> */}
                            {submitted && !username &&
                                <div className="help-block">Username is required</div>
                                }
                        </div>
                        <div className="group">
                            <input type="password" className="tpass" placeholder="Enter Password" value={password} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            {submitted && !password &&
                                <div className="help-block">Password is required</div>
                                }
                        </div>
                        <button onClick={this.handleSubmit} type="button" className="buttonui "> <span> Login </span>
                                <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                            </button>        
                        </form>
                        <div className="powered">
                        <Link to="/register" >Create New Register</Link>
                        </div>
                    </div>
              </div>
            );
          }
}
export default Login;
