import React from 'react';
import '../Registration/Registration.css';
import { Link } from 'react-router-dom';
import base64 from 'base-64';
import FlashMessage from 'react-flash-message';
import axios from 'axios';
class Registration extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            fields: {},
            errors: {},
            name:'',
            email:'',
            mobileno:'',
            verifyno:'',
            password:'',
            re_password:'',
            loading:false,
            message:false,
            request_time:'',
            otp:'',
            // data:[]

        }
        this.handleChange = this.handleChange.bind(this);
        this.sendMobileVerifyOtp = this.sendMobileVerifyOtp.bind(this);
        this.RegistrationDetailSend = this.RegistrationDetailSend.bind(this);
      }
 
      handleChange(e) {
        this.setState({ [e.target.name]: e.target.value });
      }
      
      sendMobileVerifyOtp(e) {
        e.preventDefault();
        const mobileno= this.state.mobileno
       
        this.setState({
          loading:true,
          message:true
        })
       // const passdata = mobileno
        let formData = new FormData();
        formData.append('mobile', mobileno);
        let init = {
          method: 'POST',
          headers: { 'content-type': 'multipart/form-data' },
          mode: 'cors',
          cache: 'default',
          dataType: 'json',
          type:     'POST',
          async:    true,
          
        };

        axios.post('/tps_api/index.php?view=mobile_verify',formData,init)
        .then(response => {
          const messages =  <FlashMessage duration={2000} persistOnHover={true}>
          <span className="sendmsg">OTP Send Successfully!!!</span>
            </FlashMessage>;
          //alert(response.data.MSG);
          const tm = response.data.request_time;
          const otp = response.data.data;
          //967094
          // var encoded = 'OTY3MDk0';
          var bytes = base64.decode(otp);
          // var text = utf8.decode(bytes);
          console.log("decode:->"+bytes);
        //  const hexString = otp.toString(16);
        //  alert(hexString);
        //   const yourNumber = parseInt(hexString, 16);
        //   alert(yourNumber);
          //formData.append('time', tm);
         // const items = res.data;
          // this.setState({tm});
         // console.log(response.data.request_time);
         // alert(response.data.data2);
          this.setState({
            request_time:tm,
            otp:otp
          });
          // console.log(this.state.request_time)
          // console.log(this.state.otp)
        })
        .catch(err => {
          console.log(err);
          this.setState({
            loading:false
          })
        })
  
      }

      RegistrationDetailSend(){
        let init = {
          method: 'POST',
          headers: { 'content-type': 'multipart/form-data' },
          mode: 'cors',
          cache: 'default',
          dataType: 'json',
          type:     'POST',
          async:    true,
        };

        const name= this.state.name
        const email= this.state.email
        const mobileno= this.state.mobileno
        const verifyno= this.state.verifyno
        const password= this.state.password
        const re_password= this.state.re_password
        
        let formData = new FormData();

        formData.append('name', name);
        formData.append('email', email);
        formData.append('mobile', mobileno);
        formData.append('verifyno', verifyno);
        formData.append('password', password);
        formData.append('re_password', re_password);
        
        
        
       
        axios.post('/tps_api/index.php?view=mobile_verify',formData,init)
        .then(response => {
          console.log(response.data)
        })
        .catch(err => {
          console.log(err);
          this.setState({
            loading:false
          })
        })

      }
      
  
    render() {
    
            return (
              
              <div className="register">
          
                   <div className="background1"></div>
                  <div className="background3"></div>
                {/* {this.loadingOrmessage()} */}
                   <div className="registerForm"> 
                        <hgroup>
                        <h1>True Transport Register</h1>
                        {/* <span class="icon novi-icon icon-circle icon-bordered icon-lg icon-default mdi mdi-account-multiple-outline"></span> */}
                        </hgroup>
                        <form encType="multipart/form-data">
                        <div className="group">
                            <input type="text" placeholder="Name" name="name" className="tname" value={this.state.name} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Username</label> */}
                            {/* <span className="errorMsg">{this.state.errors.name}</span> */}
                        </div>
                        <div className="group">
                            <input type="email" placeholder="Email" name="email" className="temail" value={this.state.email} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Username</label> */}
                            {/* <span className="errorMsg">{this.state.errors.email}</span> */}
                        </div>
                        <div className="group">
                            <input type="text" placeholder="Mobile No" name="mobileno" className="tmobile" value={this.state.mobileno} onChange={this.handleChange}/><span className="highlight"></span><span className="rbar"></span>
                            {/* <label>Password</label> */}
                            <button onClick= {this.sendMobileVerifyOtp} className="btn btn-deluge btn-sm" id="verifymobileno" name="verifymobileno" type="button">Send OTP</button>
                            { this.state.message && this.state.mobileno !='' && (<span className="sendmsg">OTP Send Successfully!!!</span>) }
                            {/* {  this.state.message ? <span className="sendmsg">OTP Send Successfully!!!</span>:<span className="sendmsg">OTP Send Successfully!!!asdf</span>} */}
                            {/* <span className="errorMsg">{this.state.errors.mobileno}</span> */}
                        </div>
                        <div className="group">
                            <input type="text" placeholder="Verify Code" name="verifyno" className="tvcode" value={this.state.verify} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            {/* <span className="errorMsg">{this.state.errors.verifyno}</span> */}
                        </div>
                        <div className="group">
                            <input type="password"  placeholder="Password" name="password" className="tpass" value={this.state.password} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            {/* <span className="errorMsg">{this.state.errors.password}</span> */}
                        </div>
                        <div className="group">
                            <input type="password" placeholder="Confirm Password" name="re_password" className="tpass" value={this.state.re_password} onChange={this.handleChange}/><span className="highlight"></span><span className="bar"></span>
                            {/* <label>Password</label> */}
                            {/* <span className="errorMsg">{this.state.errors.password}</span> */}
                        </div>
                            <button onClick={this.RegistrationDetailSend} type="button" className="buttonui "> <span> Register </span>
                                <div className="ripples buttonRipples"><span className="ripplesCircle"></span></div>
                            </button>     
                        </form>
                        <div className="powered">
                        You have Already Account?<Link to="/" >Login</Link>
                        </div>

                    </div>
              </div>
            );
          }
}

export default Registration;
